import { useEffect, useState } from "react";

export interface Message {
  title: string;
  role: "user" | "assistant";
  content: string;
}
const defaultMessage: Message = {
  title: "",
  role: "user",
  content: "",
};

const App = () => {
  const [previousMessages, setPreviousMessages] = useState<Message[]>([]);
  const [message, setMessage] = useState<Message>(defaultMessage);
  const [value, setValue] = useState<string>("");
  const [currentTitle, setCurrentTitle] = useState<string>("");
  const [messageContent, setMessageContent] = useState<string>("");

  const getMessages = async () => {
    try {
      const options = {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          messages: value,
        }),
      };

      const response = await fetch(
        "http://localhost:8000/completions",
        options
      );
      const data = await response.json();
      console.log("data :>> ", data);
      setMessage(data.choices[0].message);
    } catch (error) {
      console.error("error :>> ", error);
    }
  };

  useEffect(() => {
    // We're initiating a new chat
    if (!currentTitle && messageContent && message) {
      setCurrentTitle(messageContent);
    }

    // We're in an existing chat
    if (currentTitle && messageContent && message) {
      setPreviousMessages(
        (prev) =>
          [
            ...prev,
            {
              title: currentTitle,
              role: "user",
              message: messageContent,
            },
            {
              title: currentTitle,
              role: message.role,
              content: message.content,
            },
          ] as Message[]
      );
    }
  }, [message, currentTitle]);

  return (
    <div className='app'>
      <section className='side-bar'>
        <button>+ New chat</button>
        <ul className='history'>
          <li>DAMMY CHAT </li>
        </ul>
        <nav>
          <p className='powered-by'>Github @LiorKasti</p>
        </nav>
      </section>
      <section className='main'>
        {!currentTitle && <h1>KST-GPT</h1>}
        <ul className='feed'>
          <div className='bottom-section'>
            <div className='input-container'>
              <input value={value} onChange={(e) => setValue(e.target.value)} />
              <div id='submit' onClick={getMessages}>
                âž¢
              </div>
            </div>
            <p className='info'>
              ChatGPT is a free-to-use AI system. Use it for engaging
              conversations, gain insights, automate tasks, and witness the
              future of AI, all in one place.
            </p>
          </div>
        </ul>
      </section>
    </div>
  );
};

export default App;
